const topic = msg.topic || "";
const m = topic.match(/^ebike\/bikes\/([^\/]+)\/telemetry$/);
const bikeId = m ? m[1] : "unknown";

if (typeof msg.payload === "string") {
  try {
    msg.payload = JSON.parse(msg.payload);
  } catch (e) {
    return null; 
  }
}

const t = msg.payload?.telemetry ?? null;
if (!t || typeof t !== "object") return null;

if (t.battery === undefined || t.lat === undefined || t.lon === undefined) return null;

msg.payload = {
  id: bikeId,
  battery: Number(t.battery),
  motor_locked: !!t.motor_locked,
  is_charging: !!t.is_charging,
  is_available: !!t.is_available,
  lat: Number(t.lat),
  lon: Number(t.lon)
};

return msg;

const B_LOW = 20;
const B_RESET = 25;   
const B_FULL = 95;

let alerts = [];
const b = msg.payload.battery;

if (b <= B_LOW && !msg.payload.is_charging) alerts.push("LOW_BATTERY");
if (b >= B_FULL && msg.payload.is_charging) alerts.push("CHARGED");

msg._reset_low = (b >= B_RESET);

msg.alerts = alerts;
return msg;

const id = msg.payload.id;
let cmds = [];

// ---- LOW_BATTERY gating ----
const lowKey = `low_sent_${id}`;
const lowAlready = context.get(lowKey) || false;

if (msg.alerts.includes("LOW_BATTERY")) {
  if (!lowAlready) {
    cmds.push({
      topic: `ebike/bikes/${id}/commands`,
      payload: { request: "CHARGE", lat: 42.3540, lon: 13.3910 }
    });
    cmds.push({
      topic: `ebike/bikes/${id}/commands`,
      payload: { request: "BALANCE", rate: 5 }
    });
    context.set(lowKey, true);
  }
} else {
  if (lowAlready && msg._reset_low) {
    context.set(lowKey, false);
  }
}

// ---- CHARGED gating ----
const chargedKey = `charged_sent_${id}`;
const chargedAlready = context.get(chargedKey) || false;

if (msg.alerts.includes("CHARGED")) {
  if (!chargedAlready) {
    cmds.push({
      topic: `ebike/bikes/${id}/commands`,
      payload: { request: "AVAILABLE" }
    });
    cmds.push({
      topic: `ebike/bikes/${id}/commands`,
      payload: { request: "LOCK" }
    });
    context.set(chargedKey, true);
  }
} else {
  if (chargedAlready) {
    context.set(chargedKey, false);
  }
}

if (cmds.length === 0) return null;

if (cmds.length === 0) return null;

msg.payload = cmds.map(c => ({
  topic: c.topic,
  payload: { ...c.payload, source: "node-red" }
}));

return msg;

const WINDOW_MS = 60 * 1000; // 60s (puedes bajar a 10s si quieres)

if (!msg.payload || typeof msg.payload !== "object") return null;

// Normalize
const outTopic = msg.payload.topic;
const outPayload = msg.payload.payload;

if (!outTopic || !outPayload || !outPayload.request) {
  return null;
}

msg.topic = outTopic;
msg.payload = outPayload;

const key = `${msg.topic}|${msg.payload.request}`;
const now = Date.now();

let lastSent = context.get("lastSent") || {};
if (lastSent[key] && (now - lastSent[key]) < WINDOW_MS) {
  return null; 
}

lastSent[key] = now;
context.set("lastSent", lastSent);

return msg;

// Escribe en el measurement que Grafana ya está consultando:
msg.measurement = "bikes";

// Bike id (el dashboard espera el tag bike_id)
const bikeId = String(msg.payload.id ?? msg.topic?.split("/")[2] ?? "unknown");

// Fields (valores)
const fields = {
  battery: Number(msg.payload.battery),
  lat: Number(msg.payload.lat),
  lon: Number(msg.payload.lon),
  is_available: Boolean(msg.payload.is_available),
  is_charging: Boolean(msg.payload.is_charging),
  motor_locked: Boolean(msg.payload.motor_locked),
};

// Tags (Grafana usa bike_id)
const tags = { bike_id: bikeId };

// Formato correcto para node-red-contrib-influxdb: [fields, tags]
msg.payload = [fields, tags];

return msg;
